import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import {useState} from "react";

function App() {
let count = 0


function Decrement() {
  const [count, updatedCount] = useState("0")
  return (
    <>
    <p>{count}</p>
    <button type='button' onClick={() => updatedCount("-1")}></button>
    </>
  )
}


const clear = () => {
  alert("Nothing here");
}

const increment = () => {
  alert("Plus value!");
}


  return (
    <div className="App">
      <h1 className='counter-title text-center'>Лічильник</h1>
      <br />
        <div className="text-center">
          <p className='w-50 p-3 d-inline border border-dark text-justify'>{count}</p>
        </div>
        <br />
        <br />
        <div class="container">
  <div class="row">
    <div class="col">
    <button className="w-25 p-3 d-inline-flex btn btn-outline-primary btn-sm text-center px-2" onClick={Decrement}>Зменшити</button>
    </div>
    <div class="col">
    <button className="w-25 p-3 d-inline-flex btn btn-outline-primary btn-sm text-center px-2" onClick={clear}>Очистити</button>
    </div>
    <div class="col">
    <button className="w-25 p-3 d-inline-flex btn btn-outline-primary btn-sm text-center px-2" onClick={increment}>Збільшити</button>
    </div>
  </div>
</div>
    </div>

  );
}

export default App;
